import react, {FC, useState, useEffect} from 'react'
import axios from 'axios'
import './lookup.css'

interface IProps{
    mode: string;
}
const LookUp:FC<IProps> = (props:IProps) => {

    const [data, setData] = useState<any>();
    const [text, setText] = useState<string>('');
    const [searchMode, setSearchMode] = useState<boolean>(false);


    useEffect( () => {
        const url = 'https://swapi.dev/api/' + props.mode.toLowerCase() + '/?search=' + text
        axios.get(url)
             .then( (response => setData(response.data)))
    }, [text]);

    return(
        <div className="LookUpContainer">
            <input type="text" className="LookUp" onChange={(e) => setText(e.target.value)} onClick={()=>setSearchMode(true)}/>
            <input type="reset" value="x" className="CloseSearch" onClick={() => {setSearchMode(false); }}/>
            {data && console.log(searchMode, "Text", text)}
        </div>    
    );
}

export default LookUp;
