import react, {FC} from 'react'
import { useQuery, gql } from '@apollo/client';
import './films.css'

const FILMS = gql`
    query getFilms{
        allFilms{
            films{
            title
            director
            episodeID
            releaseDate
            }
        }
    }
`;


const Films:FC = () => {
    const { loading, error, data } = useQuery(FILMS);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    return(
        <div>{data}aa</div>
    );
}

export default Films;

