import react, {FC, useState, useEffect} from 'react'
import './planets.css'
import axios from 'axios'


interface IPlanets {
    results: IPlanet[]
}

interface IPlanet {
    characters: string[]
    name: string
    rotation_period: string
    orbital_period: string
    diameter: string
    climate: string 
    gravity: string
    terrain: string 
    surface_water: string 
    population: string 
    residents: string[]
    url: string
}

const Planets:FC = () => {

    const [data, setData] = useState<IPlanets>();

    useEffect( () => {
        axios.get('https://swapi.dev/api/planets')
             .then( (response => setData(response.data)))
    }, []);



    return(
        <div>
            { !data && <p> Loading... </p>}
            {data && data.results.map( (item:IPlanet) => {
                return(
                    <div className="Target">
                        <h3>{item.title}</h3>
                        <h2>{item.director}</h2>
                        <p>{item.release_date}</p>
                        <p>{item.opening_crawl.substring(0,180)} ...</p>
                    </div>
                );
            })}
        </div>

    );
}

export default Planets;
