import react, {FC, useState, useEffect} from 'react'
import axios from 'axios'
import './lookup.css'

interface IProps{
    mode: string;
}
const LookUp:FC<IProps> = (props:IProps) => {

    const [data, setData] = useState<any>();
    const [text, setText] = useState<string>('');
    const [mode, setMode] = useState<string>('films')
    //setMode(props.mode.toLowerCase());


    useEffect( () => {
        const url = 'https://swapi.dev/api/' + mode + '/?search=' + 'text'
        axios.get(url)
             .then( (response => setData(response.data)))
    }, []);



    return(
        <div className="LookUpContainer">
            <input type="text" className="LookUp" onChange={(e) => setText(e.target.value)}/>
            {data && mode=='films' && console.log('HEEEEEY"', data)}
        </div>



    );
}

export default LookUp;
