import react, {FC, useState, useEffect} from 'react'
import './planets.css'
import axios from 'axios'


interface IPlanets {
    results: IPlanet[]
    info: IInfo[];
}

interface IPlanet {
    films: string[]
    name: string
    rotation_period: string
    orbital_period: string
    diameter: string
    climate: string 
    gravity: string
    terrain: string 
    surface_water: string 
    population: string 
    residents: string[]
    url: string
}

const Planets:FC = () => {

    const [data, setData] = useState<IPlanets>();

    useEffect( () => {
        axios.get('https://swapi.dev/api/planets')
             .then( (response => setData(response.data)))
    }, []);



    return(
        <div>
            { !data && <p> Loading... </p>}
            {data && data.results.map( (item:IPlanet) => {
                return(
                    <div className="Target">
                        <h3>{item.name}</h3>
                        <h2>Population: {item.population}</h2>
                        <p>Period: [rotation-{item.rotation_period}, orbital-{item.orbital_period}]</p>
                        <p>Climate: {item.climate}</p>
                        <p>Gravity: {item.gravity}</p>
                        <p>Terrain: {item.terrain}</p>
                        <p>Surface water: {item.surface_water}</p>
                    </div>
                );
            })}
        </div>

    );
}

export default Planets;
