import react, {FC, useState, useEffect} from 'react'
import axios from 'axios'
import './lookup.css'

interface IProps{
    mode: string;
}
const LookUp:FC<IProps> = (props:IProps) => {

    const [data, setData] = useState<any>();
    const [text, setText] = useState<string>('');
    //setMode(props.mode.toLowerCase());


    useEffect( () => {
        const url = 'https://swapi.dev/api/' + props.mode.toLowerCase() + '/?search=' + text
        axios.get(url)
             .then( (response => setData(response.data)))
    }, [text]);



    return(
        <div className="LookUpContainer">
            <input type="text" className="LookUp" onChange={(e) => setText(e.target.value)}/>
            {data && props.mode.toLowerCase()=='films' && console.log('HEEEEEY"', text, data)}
            {data && props.mode.toLowerCase()=='people' && console.log('HEEEEEY"', text, data)}
            {data && props.mode.toLowerCase()=='planets' && console.log('HEEEEEY"', text, data)}
        </div>



    );
}

export default LookUp;
