import react, {FC, useState, useEffect} from 'react'
import './characters.css'
import axios from 'axios'


interface ICharacters {
    results: ICharacter[]
}

interface ICharacter {
    films: string[]
    name: string
    height: string
    mass: string
    hair_color: string
    skin_color: string
    eye_color: string
    birth_year: string
    gender: string
    homeworld: string
    species: string[]
    starships: string[]
    url: string
    vehicles: string[]
}

const Characters:FC = () => {

    const [data, setData] = useState<ICharacters>();

    useEffect( () => {
        axios.get('https://swapi.dev/api/people')
             .then( (response => setData(response.data)))
    }, []);



    return(
        <div>
            { !data && <p> Loading... </p>}
            {data && data.results.map( (item:ICharacter) => {
                return(
                    <div className="Target">
                        <h3>{item.name}</h3>
                        <p>{item.gender}</p>
                        <p>{item.skin_color}</p>
                        <p>{item.height}cm</p>
                    </div>
                );
            })}
        </div>

    );
}

export default Characters;
