import react, {FC, useState, useEffect} from 'react'
import './films.css'
import axios from 'axios'


interface IFilms {
    results: IFilm[]
    count: number
    next: string | null
    previous: string | null
}

interface IFilm {
    characters: string[]
    created: string
    director: string
    edited: string
    episode_id: number
    opening_crawl: string
    planets: string[]
    producer: string
    release_date: string
    species: string[]
    starships: string[]
    title: string
    url: string
    vehicles: string[]
}

interface IFilmsProps{
    order: string
}

const Films:FC<IFilmsProps> = (props:IFilmsProps) => {

    const order = props.order;

    const [data, setData] = useState<IFilms>();

    useEffect( () => {
        axios.get('https://swapi.dev/api/films')
             .then( (response => setData(response.data)))
    }, []);

    if(data) console.log(data.results[0]);

    if(data && order=='DESC') {data.results.sort(function(a,b) { if (a.title < b.title) return 1; if (a.title > b.title) return -1; else{return 0;}}) }//using descending order to sort
    if(data && order=='ASC') {data.results.sort(function(a,b) { if (a.title > b.title) return 1; if (a.title < b.title) return -1; else{return 0;}}) }//using ascending order to sort

    return(
        <div>
            { !data && <p> Loading... </p>}

            {data && data.results.map( (item:IFilm) => {
                return(
                    <div className="Target">
                        <h3>{item.title}</h3>
                        <h2>{item.director}</h2>
                        <p>{item.release_date}</p>
                        <p>{item.opening_crawl.substring(0,180)} ...</p>
                    </div>
                );
            })}
        </div>

    );
}

export default Films;
