import react, {FC, useState, useEffect} from 'react'
import './films.css'
import axios from 'axios'


interface IFilms {
    results: IFilm[]
    count: number
    next: string | null
    previous: string | null
}

interface IFilm {
    characters: string[]
    created: string
    director: string
    edited: string
    episode_id: number
    opening_crawl: string
    planets: string[]
    producer: string
    release_date: string
    species: string[]
    starships: string[]
    title: string
    url: string
    vehicles: string[]
}

const Films:FC = () => {

    const [data, setData] = useState<IFilms>();

    useEffect( () => {
        axios.get('https://swapi.dev/api/films')
             .then( (response => setData(response.data)))
    }, []);



    return(
        <div>
            { !data && <p> Loading... </p>}
            {data && data.results.map( (item:IFilm) => {
                return(
                    <div className="Target">
                        <h3>{item.title}</h3>
                        <h2>{item.director}</h2>
                        <p>{item.release_date}</p>
                        <p>{item.opening_crawl.substring(0,180)} ...</p>
                    </div>
                );
            })}
        </div>

    );
}

export default Films;
